mapper.select:
SELECT
 wg.id
FROM
we_group wg
left join we_group_tag_rel wgtr  on wg.chat_id=wgtr.chat_id and wgtr.del_flag = 0
left join we_tag  wt  on wt.id = wgtr.tag_id
left join sys_user su on su.we_user_id = wg.owner and su.del_flag = 0
where wg.del_flag=0

    and wg.id = #{query.id}


    and wg.chat_id in

        #{item}



    AND wg.group_name  LIKE CONCAT('%',#{query.groupName},'%')



    and su.user_name like concat('%', #{query.groupLeaderName}, '%')


    AND  date_format(wg.add_time,'%Y-%m-%d') BETWEEN #{query.beginTime} AND #{query.endTime}






        AND wg.`owner` in

            #{item}



        AND wg.`owner`=#{query.userIds}



    AND wt.`id` in

        #{item}


GROUP BY wg.id
ORDER BY wg.add_time desc


        limit ${pageDomain.pageSize * (pageDomain.pageNum-1)}, #{pageDomain.pageSize}

SELECT count(DISTINCT wg.id) FROM we_group wg left join we_group_tag_rel wgtr on wg.chat_id=wgtr.chat_id and wgtr.del_flag = 0 left join we_tag wt on wt.id = wgtr.tag_id left join sys_user su on su.we_user_id = wg.owner and su.del_flag = 0 where wg.del_flag=0
and wg.id = #{query.id}
and wg.chat_id in
#{item}
AND CONCAT( IFNULL(wg.group_name,''),IFNULL(su.user_name,'')) LIKE CONCAT('%',#{query.groupName,jdbcType=VARCHAR},'%')
and su.user_name like concat('%', #{query.groupLeaderName}, '%')
AND date_format(wg.add_time,'%y%m%d') >= date_format(#{query.beginTime},'%y%m%d')
AND date_format(wg.add_time,'%y%m%d') <= date_format(#{query.endTime},'%y%m%d')
AND wg.`owner` in
#{item}
AND wg.`owner`=#{query.userIds}
AND wt.`id` in
#{tagId}

SELECT wg.id, wg.chat_id, wg.group_name, wg.add_time, wg.create_time, wg.notice, wg.status, wg.`owner`, IFNULL(su.user_name,wg.`owner`) as groupLeaderName, count(DISTINCT wgm.user_id) as memberNum, count(DISTINCT IF(wgm.type = 2,1, 0)) as customerNum, sum(IF(date_format(wgm.join_time, '%Y-%m-%d') = date_format(now(), '%Y-%m-%d') and wgm.quit_scene IS NULL, 1, 0)) as toDayMemberNum, sum(IF(date_format(wgm.join_time, '%Y-%m-%d') = date_format(now(), '%Y-%m-%d') and wgm.quit_scene IS NOT NULL, 1, 0)) as toDayExitMemberNum, GROUP_CONCAT(distinct wt.`name`) as tags, GROUP_CONCAT(distinct wt.`id`) as tagIds FROM we_group wg left join we_group_member wgm on wgm.chat_id=wg.chat_id and wgm.del_flag=0 left join we_group_tag_rel wgtr on wg.chat_id=wgtr.chat_id and wgtr.del_flag = 0 left join we_tag wt on wt.id = wgtr.tag_id left join sys_user su on su.we_user_id = wg.owner and su.del_flag = 0 where wg.del_flag=0
and wg.id = #{id}
and wg.chat_id in
#{item}
AND CONCAT( IFNULL(wg.group_name,''),IFNULL(su.user_name,'')) LIKE CONCAT('%',#{groupName,jdbcType=VARCHAR},'%')
and su.user_name like concat('%', #{groupLeaderName}, '%')
AND date_format(wg.add_time,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')
AND date_format(wg.add_time,'%y%m%d') <= date_format(#{endTime},'%y%m%d')
AND wg.`owner` in
#{item}
AND wg.`owner`=#{userIds}
AND wt.`id` in
#{tagId}
group by wg.id order by wg.add_time desc

SELECT wg.chat_id, wg.add_time, wg.group_name, GROUP_CONCAT(DISTINCT su.user_name) as groupLeaderName, (SELECT count(DISTINCT wgm.user_id) FROM we_group_member wgm WHERE wgm.chat_id=wg.chat_id and wgm.del_flag=0 ) as memberNum FROM we_group wg LEFT JOIN we_group_tag_rel wgtr ON wg.chat_id=wgtr.chat_id and wgtr.del_flag = 0 LEFT JOIN sys_user su ON su.we_user_id=wg.owner and su.del_flag = 0 WHERE wg.del_flag=0
AND CONCAT( IFNULL(wg.group_name,''),IFNULL(su.user_name,'')) LIKE CONCAT('%',#{groupName,jdbcType=VARCHAR},'%')
AND wgtr.tag_id in
#{item}
AND date_format(wg.add_time,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')
AND date_format(wg.add_time,'%y%m%d') <= date_format(#{endTime},'%y%m%d')
AND wg.`owner` in
#{item}
AND wg.`owner`=#{userIds}
GROUP BY wg.chat_id

mapper.insert:
insert into we_group (chat_id, group_name, owner, add_time, notice, status,admin_user_id, create_by, create_time,update_by, update_time,del_flag,create_by_id, update_by_id) values
(#{weGroup.chatId},#{weGroup.groupName},#{weGroup.owner},#{weGroup.addTime},#{weGroup.notice},#{weGroup.status},#{weGroup.adminUserId}, #{weGroup.createBy},#{weGroup.createTime},#{weGroup.updateBy},#{weGroup.updateTime},#{weGroup.delFlag},#{weGroup.createById},#{weGroup.updateById})
on duplicate key update group_name= values(group_name),owner= values(owner), notice= values(notice),status= values(status),admin_user_id= values(admin_user_id), del_flag= values(del_flag), update_by= values(update_by),update_time= values(update_time),update_by_id= values(update_by_id);

