#{item}

SELECT wg.chat_id, wg.add_time, wgm.user_id as memberId, (SELECT count(wgmm.id) from we_group_member wgmm where wgmm.chat_id=wg.chat_id and wgmm.quit_scene IS NULL) as memberNum FROM we_group wg LEFT JOIN we_group_member wgm ON wg.chat_id = wgm.chat_id and wgm.quit_scene is NULL LEFT JOIN we_group_tag_rel wgtr ON wgtr.chat_id=wg.chat_id and wgtr.del_flag=0 where wg.del_flag=0
AND date_format(wg.add_time,'%Y-%m-%d') BETWEEN #{weCustomerList.beginTime} AND #{weCustomerList.endTime}
AND wgtr.tag_id in
#{item}
and wgm.user_id in
#{item}
and memberNum BETWEEN #{query.groupMemberUp} and #{query.groupMemberDown}
and wg.chat_id=#{query.chatId}
