mapper.select:
select
    (select count(1) from we_customer where del_flag = 0


        and add_user_id in

          #{weUserId}


    ) total_cnt,
    (select count(1) from we_customer where date_format(add_time,'%y%m%d') = date_format(curdate(),'%y%m%d')

        and add_user_id in

            #{weUserId}


    ) as td_cnt,
    (select count(1) from we_customer where date_format(track_time,'%y%m%d') = date_format(curdate(),'%y%m%d')

        and add_user_id in

            #{weUserId}


    ) as td_follow_up_customer,
    (select count(distinct external_userid) from we_customer where date_format(add_time,'%y%m%d') = date_format(curdate(),'%y%m%d')  and del_flag = 0

        and add_user_id in

            #{weUserId}


    ) as td_net_cnt,
    (select count(1) from we_customer where date_format(update_time,'%y%m%d') = date_format(curdate(),'%y%m%d')  and track_state = 5

        and add_user_id in

            #{weUserId}


    ) as td_lost_cnt,
    (select sum(new_apply_cnt) from we_user_behavior_data where date_format(stat_time,'%y%m%d') = date_format(date_sub(curdate(),interval 1 day),'%y%m%d')

            and user_id in

                #{weUserId}


    ) as yd_new_apply_cnt

select x_time, @total := @total + pure_add_cnt AS total_cnt from (select date_list.`date` as x_time, IFNULL(real_data.pure_add_num, 0) as pure_add_cnt from ( select `date` from sys_dim_date where `date` >= DATE_FORMAT(#{beginTime},'%Y-%m-%d') and `date` <= DATE_FORMAT(#{endTime},'%Y-%m-%d') ) as date_list left join (select DATE_FORMAT(wc.add_time, '%Y-%m-%d') day, count(1) as pure_add_num from we_customer wc WHERE wc.del_flag = 0
and wc.add_user_id in
#{userId}
and exists(select 1 from sys_user su where wc.add_user_id = su.we_user_id and su.dept_id in
#{deptId}
)
and DATE_FORMAT(wc.add_time, '%Y-%m-%d') >= DATE_FORMAT(#{beginTime}, '%Y-%m-%d') and DATE_FORMAT(wc.add_time, '%Y-%m-%d') <= DATE_FORMAT(#{endTime}, '%Y-%m-%d') group by day, (SELECT @total := (select count(1) from we_customer wct where wct.del_flag = 0
and wct.add_user_id in
#{userId}
and exists(select 1 from sys_user su where wct.add_user_id = su.we_user_id and su.dept_id in
#{deptId}
)
and DATE_FORMAT(wct.add_time, '%Y-%m-%d') < DATE_FORMAT(#{beginTime}, '%Y-%m-%d')))) as real_data on date_list.`date` = real_data.day order by day asc) as result order by x_time asc

select x_time, @total := @total + pure_add_cnt AS total_cnt, add_cnt, net_cnt, apply_cnt, gjCnt from ( select date_list.`date` as x_time , IFNULL(real_data.add_num, 0) as add_cnt, IFNULL(real_data.pure_add_num, 0) as pure_add_cnt, IFNULL(real_data.net_num, 0) as net_cnt, IFNULL( real_data.gjCnt, 0 ) AS gjCnt, 0 as apply_cnt from ( select `date` from sys_dim_date where `date` >= DATE_FORMAT(#{beginTime},'%Y-%m-%d') and `date` <= DATE_FORMAT(#{endTime},'%Y-%m-%d') ) as date_list left join (select DATE_FORMAT(wc.add_time, '%Y-%m-%d') day, count(1) as add_num, count(if(wc.track_state=2,1,0)) AS gjCnt, sum(case when wc.del_flag = 0 then 1 else 0 end) as pure_add_num, count(distinct case when wc.del_flag = 0 then wc.external_userid end) as net_num from we_customer wc
and wc.add_user_id in
#{userId}
and exists(select 1 from sys_user su where wc.add_user_id = su.we_user_id and su.dept_id in
#{deptId}
)
and DATE_FORMAT(wc.add_time,'%Y-%m-%d') >= DATE_FORMAT(#{beginTime},'%Y-%m-%d') and DATE_FORMAT(wc.add_time,'%Y-%m-%d') <= DATE_FORMAT(#{endTime},'%Y-%m-%d')
group by day ,(SELECT @total := (select count(1) from we_customer wct where wct.del_flag = 0 and DATE_FORMAT(wct.add_time,'%Y-%m-%d') < DATE_FORMAT(#{beginTime},'%Y-%m-%d')
and wct.add_user_id in
#{userId}
and exists(select 1 from sys_user su where wct.add_user_id = su.we_user_id and su.dept_id in
#{deptId}
)
))) as real_data on date_list.`date` = real_data.day order by day asc ) as result order by x_time asc

select x_time, lost_cnt from ( select date_list.`date` as x_time , IFNULL(real_data.lost_num, 0) as lost_cnt from ( select `date` from sys_dim_date where `date` >= DATE_FORMAT(#{beginTime},'%Y-%m-%d') and `date` <= DATE_FORMAT(#{endTime},'%Y-%m-%d') ) as date_list left join (select DATE_FORMAT(wc.update_time, '%Y-%m-%d') day, sum(case when wc.track_state = 5 then 1 else 0 end) as lost_num from we_customer wc
and wc.add_user_id in
#{userId}
and exists(select 1 from sys_user su where wc.add_user_id = su.we_user_id and su.dept_id in
#{deptId}
)
and DATE_FORMAT(wc.update_time,'%Y-%m-%d') >= DATE_FORMAT(#{beginTime},'%Y-%m-%d') and DATE_FORMAT(wc.update_time,'%Y-%m-%d') <= DATE_FORMAT(#{endTime},'%Y-%m-%d')
group by day) as real_data on date_list.`date` = real_data.day order by day asc ) as result order by x_time asc

choose.when:
  select
(select count(1) from we_group where del_flag = 0

    and chat_id in

        #{chatId}


) total_cnt,
(select count(1) from we_group where date_format(create_time,'%Y-%m-%d') = date_format(curdate(),'%Y-%m-%d')

    and chat_id in

        #{chatId}


) as td_group_add_cnt,
(select count(1) from we_group where date_format(create_time,'%Y-%m-%d') = date_format(curdate(),'%Y-%m-%d')  and del_flag = 1

    and chat_id in

        #{chatId}


) as td_group_dissolve_cnt,
(select count(1) from we_group_member where del_flag = 0

    and chat_id in

        #{chatId}


) as member_total_cnt,
(select count(1) from we_group_member where date_format(create_time,'%Y-%m-%d') = date_format(curdate(),'%Y-%m-%d')

    and chat_id in

        #{chatId}


) as td_member_add_cnt,
(select count(1) from we_group_member where date_format(create_time,'%Y-%m-%d') = date_format(curdate(),'%Y-%m-%d') and del_flag = 1

    and chat_id in

        #{chatId}


) as td_member_quit_cnt

mapper.select:
select x_time, @total := @total + add_cnt AS total_cnt from ( select date_list.`date` as x_time , IFNULL(real_data.add_num, 0) as add_cnt from ( select `date` from sys_dim_date where `date` >= DATE_FORMAT(#{beginTime},'%Y-%m-%d') and `date` <= DATE_FORMAT(#{endTime},'%Y-%m-%d') ) as date_list left join (select DATE_FORMAT(wg.create_time, '%Y-%m-%d') day, count(1) as add_num from we_group wg where 1=1 and wg.del_flag = 0
and wg.owner in
#{ownerId}
and DATE_FORMAT(wg.create_time,'%Y-%m-%d') >= DATE_FORMAT(#{beginTime},'%Y-%m-%d') and DATE_FORMAT(wg.create_time,'%Y-%m-%d') <= DATE_FORMAT(#{endTime},'%Y-%m-%d') group by day ,(SELECT @total := (select count(1) from we_group wgt where wgt.del_flag = 0 and date_format(wgt.create_time,'%Y-%m-%d') < DATE_FORMAT(#{beginTime},'%Y-%m-%d')
and wgt.owner in
#{ownerId}
))) as real_data on date_list.`date` = real_data.day order by x_time asc ) as result order by x_time asc

select x_time, @total := @total + add_cnt AS total_cnt from ( select date_list.`date` as x_time , IFNULL(real_data.add_num, 0) as add_cnt from ( select `date` from sys_dim_date where `date` >= DATE_FORMAT(#{beginTime},'%Y-%m-%d') and `date` <= DATE_FORMAT(#{endTime},'%Y-%m-%d') ) as date_list left join (select DATE_FORMAT(wgm.join_time, '%Y-%m-%d') day, count(1) as add_num from we_group_member wgm where 1=1 and wgm.del_flag = 0
and wgm.chat_id in
#{chatId}
and exists (select 1 from we_group wg where wg.chat_id = wgm.chat_id and wg.del_flag = 0 and wg.owner in
#{ownerId}
)
and DATE_FORMAT(wgm.join_time,'%Y-%m-%d') >= DATE_FORMAT(#{beginTime},'%Y-%m-%d') and DATE_FORMAT(wgm.join_time,'%Y-%m-%d') <= DATE_FORMAT(#{endTime},'%Y-%m-%d') group by day ,(SELECT @total := (select count(1) from we_group_member wgmt where wgmt.del_flag = 0 and date_format(wgmt.join_time,'%Y-%m-%d') < DATE_FORMAT(#{beginTime},'%Y-%m-%d')
and wgmt.chat_id in
#{chatId}
and exists (select 1 from we_group wg where wg.chat_id = wgmt.chat_id and wg.del_flag = 0 and wg.owner in
#{ownerId}
)
))) as real_data on date_list.`date` = real_data.day order by x_time asc ) as result order by x_time asc

select x_time, @total := @total + add_cnt AS total_cnt, add_cnt, dissolve_cnt from ( select date_list.`date` as x_time , IFNULL(real_data.add_num, 0) as add_cnt, IFNULL(real_data.dissolve_num, 0) as dissolve_cnt from ( select `date` from sys_dim_date where `date` >= DATE_FORMAT(#{beginTime},'%Y-%m-%d') and `date` <= DATE_FORMAT(#{endTime},'%Y-%m-%d') ) as date_list left join (select DATE_FORMAT(wg.create_time, '%Y-%m-%d') day, count(1) as add_num, sum(case when wg.del_flag = 1 then 1 else 0 end) as dissolve_num from we_group wg where 1=1
and wg.owner in
#{ownerId}
and DATE_FORMAT(wg.create_time,'%Y-%m-%d') >= DATE_FORMAT(#{beginTime},'%Y-%m-%d') and DATE_FORMAT(wg.create_time,'%Y-%m-%d') <= DATE_FORMAT(#{endTime},'%Y-%m-%d') group by day ,(SELECT @total := (select count(1) from we_group wgt where wgt.del_flag = 0 and date_format(wgt.create_time,'%Y-%m-%d') < DATE_FORMAT(#{beginTime},'%Y-%m-%d')
and wgt.owner in
#{ownerId}
))) as real_data on date_list.`date` = real_data.day order by x_time asc ) as result order by x_time asc

select x_time, @total := @total + add_cnt AS total_cnt, add_cnt, quit_cnt from ( select date_list.`date` as x_time , IFNULL(real_data.add_num, 0) as add_cnt, IFNULL(quit_data.quit_num, 0) as quit_cnt from ( select `date` from sys_dim_date where `date` >= DATE_FORMAT(#{beginTime},'%Y-%m-%d') and `date` <= DATE_FORMAT(#{endTime},'%Y-%m-%d') ) as date_list left join (select DATE_FORMAT(wgm.join_time, '%Y-%m-%d') day, count(1) as add_num from we_group_member wgm where 1=1 and wgm.del_flag = 0
and wgm.chat_id in
#{chatId}
and exists (select 1 from we_group wg where wg.chat_id = wgm.chat_id and wg.del_flag = 0 and wg.owner in
#{ownerId}
)
and DATE_FORMAT(wgm.join_time,'%Y-%m-%d') >= DATE_FORMAT(#{beginTime},'%Y-%m-%d') and DATE_FORMAT(wgm.join_time,'%Y-%m-%d') <= DATE_FORMAT(#{endTime},'%Y-%m-%d') group by day ,(SELECT @total := (select count(1) from we_group_member wgmt where wgmt.del_flag = 0 and date_format(wgmt.join_time,'%Y-%m-%d') < DATE_FORMAT(#{beginTime},'%Y-%m-%d')
and wgmt.chat_id in
#{chatId}
and exists (select 1 from we_group wg where wg.chat_id = wgmt.chat_id and wg.del_flag = 0 and wg.owner in
#{ownerId}
)
))) as real_data on date_list.`date` = real_data.day left join (select DATE_FORMAT(wgm.quit_time, '%Y-%m-%d') day, count(1) as quit_num from we_group_member wgm where 1=1
and wgm.chat_id in
#{chatId}
and exists (select 1 from we_group wg where wg.chat_id = wgm.chat_id and wg.del_flag = 0 and wg.owner in
#{ownerId}
)
and DATE_FORMAT(wgm.quit_time,'%Y-%m-%d') >= DATE_FORMAT(#{beginTime},'%Y-%m-%d') and DATE_FORMAT(wgm.quit_time,'%Y-%m-%d') <= DATE_FORMAT(#{endTime},'%Y-%m-%d') group by day) as quit_data on date_list.`date` = quit_data.day ) as result order by x_time asc

select date_format(stat_time,'%Y-%m-%d') as x_time, sum(wubd.chat_cnt) as chat_cnt, sum(wubd.message_cnt) as message_cnt, avg(wubd.reply_percentage) as reply_percentage, avg(wubd.avg_reply_time) as avg_reply_time from we_user_behavior_data wubd
and wubd.user_id in
#{userId}
and exists (select 1 from sys_user wu where wu.we_user_id = wubd.user_id and wu.department in
#{deptId}
)
and date_format(stat_time,'%Y-%m-%d') >= date_format(#{beginTime},'%Y-%m-%d') and date_format(stat_time,'%Y-%m-%d') <= date_format(#{endTime},'%Y-%m-%d')
group by wubd.stat_time order by x_time
