mapper.select:
SELECT wset.id as executeTargetId, wc.customer_name, wc.corp_name as corpName, wc.gender, wc.customer_type, wc.avatar, wc.external_userid, wset.execute_state, su.we_user_id, su.user_name, su.position, (SELECT GROUP_CONCAT(DISTINCT sd.dept_name) FROM sys_dept sd LEFT JOIN sys_user_dept sud ON sd.dept_id=sud.dept_id where sud.we_user_id=su.we_user_id) as deptName, wset.create_time, wset.execute_end_time as executeEndTime, wset.sop_base_id, IFNULL(ROUND((SELECT COUNT(*) FROM we_sop_execute_target_attachments wseta WHERE wseta.execute_target_id=wset.id and execute_state=1)/(SELECT COUNT(*) FROM we_sop_execute_target_attachments wseta WHERE wseta.execute_target_id=wset.id )*100,0),0) as efficiency FROM we_sop_execute_target wset LEFT JOIN we_customer wc ON wset.target_id=wc.external_userid LEFT JOIN sys_user su ON su.we_user_id=wset.execute_we_user_id
wset.sop_base_id=#{sopBaseId}
and wc.customer_name like concat( '%' , #{customerName}, '%')
and wset.execute_state=#{executeState}
AND su.we_user_id=#{weUserId}
GROUP BY wset.id ORDER BY wset.create_time DESC

SELECT wset.id as executeTargetId, wg.group_name, wg.chat_id, wset.execute_state, su.we_user_id, su.user_name, su.position, (SELECT GROUP_CONCAT(DISTINCT sd.dept_name) FROM sys_dept sd LEFT JOIN sys_user_dept sud ON sd.dept_id=sud.dept_id where sud.we_user_id=su.we_user_id) as deptName, wset.create_time, wset.execute_end_time, wset.sop_base_id, IFNULL(ROUND((SELECT COUNT(*) FROM we_sop_execute_target_attachments wseta WHERE wseta.execute_target_id=wset.id and execute_state=1)/(SELECT COUNT(*) FROM we_sop_execute_target_attachments wseta WHERE wseta.execute_target_id=wset.id)*100,0),0) as efficiency FROM we_sop_execute_target wset LEFT JOIN we_group wg ON wset.target_id=wg.chat_id LEFT JOIN sys_user su ON su.we_user_id=wset.execute_we_user_id
wset.sop_base_id=#{sopBaseId}
and wg.group_name like concat( '%' , #{groupName}, '%')
and wset.execute_state=#{executeState}
AND su.we_user_id in
#{item}
ORDER BY wset.create_time DESC

SELECT wseca.push_time_type, wseca.push_time_pre, wseca.push_start_time, wseca.push_end_time, wseca.execute_state, wseca.execute_time, wseca.sop_attachment_id, wc.customer_name, wc.customer_type, wc.external_userid, wc.gender, wc.avatar, wset.sop_base_id, wsb.sop_name, wseca.id as executeTargetAttachId FROM we_sop_execute_target_attachments wseca LEFT JOIN we_sop_execute_target wset ON wseca.execute_target_id=wset.id LEFT JOIN we_sop_base wsb ON wsb.id=wset.sop_base_id LEFT JOIN we_customer wc ON wc.external_userid=wset.target_id
and wc.add_user_id=#{executeWeUserId}
WHERE wsb.del_flag=0
and to_days(wseca.push_start_time) = to_days(now()) and wsb.send_type=2
and wset.execute_we_user_id=#{executeWeUserId}
and wset.target_id=#{targetId}
and wseca.execute_state=#{executeSubState}
and wset.execute_state=1
and wset.sop_base_id=#{sopBaseId}
and wseca.execute_target_id=#{executeTargetId}

SELECT wseca.push_time_type, wseca.push_time_pre, wseca.push_start_time, wseca.push_end_time, wseca.execute_state, wseca.sop_attachment_id, wseca.execute_time, wg.group_name, wg.chat_id, wg.add_time, wset.sop_base_id, wsb.sop_name, wseca.id as executeTargetAttachId FROM we_sop_execute_target_attachments wseca LEFT JOIN we_sop_execute_target wset ON wseca.execute_target_id=wset.id LEFT JOIN we_sop_base wsb ON wsb.id=wset.sop_base_id LEFT JOIN we_group wg ON wg.chat_id=wset.target_id where wsb.sop_state=1
and to_days(wseca.push_start_time) = to_days(now()) and wsb.send_type=2
and wg.chat_id=#{chatId}
and wseca.execute_state=#{executeSubState}
and wset.execute_state=1
and wset.sop_base_id=#{sopBaseId}
and wseca.execute_target_id=#{executeTargetId}
and wset.execute_we_user_id=#{executeWeUserId}

SELECT wsb.id as sopBaseId, wsb.sop_name, wseta.push_start_time, wseta.push_end_time FROM we_sop_execute_target wset LEFT JOIN we_sop_base wsb ON wsb.id = wset.sop_base_id LEFT JOIN we_sop_execute_target_attachments wseta ON wseta.execute_target_id = wset.id WHERE to_days(wseta.push_start_time) = to_days(now()) and wseta.execute_state=0
and wset.execute_we_user_id=#{weUserId}
and wset.target_id=#{targetId}

mapper.update:
UPDATE we_sop_base
SET sop_state = #{sopState}
WHERE
    id=#{sopId}
