mapper.select:
SELECT wfir.id as fissionInviterRecordId, wgm.`name` as customerName, GROUP_CONCAT(DISTINCT wg.group_name) as chatName, su.user_name as sendUserName, wfir.inviter_state as inviterState, wfir.inviter_number as inviterNumber, wfn.target_sub_id FROM we_fission_inviter_record wfir LEFT JOIN we_fission_notice wfn ON wfir.fission_id=wfn.fission_id and wfir.inviter_unionid=wfn.target_sub_id LEFT JOIN we_group_member wgm ON wfn.target_sub_id=wgm.union_id AND wgm.del_flag=0 LEFT JOIN we_group wg ON wgm.chat_id=wg.chat_id LEFT JOIN sys_user su ON su.we_user_id=wfn.send_we_userid
wfir.fission_id=#{fissionId}
and wgm.`name` like concat('%',#{customerName},'%')
and wg.chat_id=#{chatId}
and su.we_user_id=#{weUserId}
GROUP BY wfir.inviter_unionid

SELECT DISTINCT wfn.id, wc.customer_name as oldCustomerName, su.user_name as sendWeUserName, IFNULL(wfir.inviter_state,2) AS inviterState, IFNULL(wfir.inviter_number,0) AS inviterNumber, IFNULL(wfir.id,0) AS fissionInviterRecordId FROM we_fission_inviter_record wfir LEFT JOIN we_fission_notice wfn ON wfir.fission_id = wfn.fission_id and wfir.inviter_unionid=wfn.target_sub_id LEFT JOIN we_customer wc ON wc.external_userid = wfn.target_id LEFT JOIN sys_user su ON su.we_user_id = wfn.send_we_userid
wfir.fission_id=#{fissionId}
and wc.customer_name like concat('%',#{customerName},'%')
and su.we_user_id=#{weUserId}
